mapper.update:
UPDATE task
   SET task_contents =	#{taskContents}
 WHERE task_no=#{taskNo}

UPDATE task
   SET task_label = #{color}
 WHERE task_no = #{taskNo}

mapper.select:
SELECT checklist_no AS checklistNo, checklist_contents AS checklistContents, checklist_state AS checklistState, task_no AS taskNo
  FROM checklist
 WHERE task_no = #{taskNo};
