mapper.select:
select user_no as userNo, user_email as userEmail, user_name as userName, user_password as userPassword, user_key_status as userKeyState
from user 
where user_email=#{userEmail}

select user_name as userName, user_title as userTitle, user_dept as userDept, user_photo as userPhoto
from user
where user_email =#{userEmail}


select user_name as userName, user_email as userEmail , user_password as userPassword
  from user  
 where user_email = #{userEmail}


select user_no as userNo, user_name as userName, user_email as userEmail
  from user  
 where user_email = #{userEmail }


select user_email as userEmail
  from user  
 where user_email = #{userEmail }
 	and user_name = #{userName }


select user_no as userNo, user_email as userEmail
  from user  
 where user_key = #{userKey }

mapper.update:

		update user 
		   set user_name = #{userName },
		       user_password = #{userPassword },
               user_key = null,
               user_photo = #{userPhoto}
		 where user_no = #{userNo };
