mapper.select:

 select user_no as userNo, user_name as userName, user_email as userEmail, user_photo as userPhoto, user_bg as userBg
   from user  
  where user_email = #{userEmail}
	and user_password = #{userPassword}


select user_no as userNo, user_name as userName, user_email as userEmail
  from user  
 where user_email = #{userEmail }


select user_email as userEmail
  from user  
 where user_email = #{userEmail }
 	and user_name = #{userName }


select user_no as userNo, user_email as userEmail
  from user  
 where user_key = #{userKey }

mapper.update:

		update user 
		   set user_name = #{userName },
		       user_password = #{userPassword },
               user_key = null,
               user_photo = #{userPhoto}
		 where user_no = #{userNo };
